import React, {useEffect,useState} from 'react'
import { View, Text, TouchableOpacity, Switch } from 'react-native'
import { Button, Header } from "react-native-elements"
import styles from '../../elements/style'
import debounce from 'lodash/debounce';
const HomePage: React.FC =() =>{
    const [temperature, setTemperature] = useState(null);
    useEffect(() => {
        // Replace "<your_api_key>" and "<your_feed_key>" with your Adafruit IO API key and feed key, respectively
        setTimeout(() => {
            fetch(`https://io.adafruit.com/api/v2/HarryHaha/feeds/temp-sensor/data/last`,
                {
                    headers: {
                        'X-AIO-Key': 'aio_OepY02kt3vhaTPfCPadrdw7azvV7'
                    }
                })
                .then(response => response.json())
                .then(data => setTemperature(data.value))
                .catch(error => console.error(error));
        }, 500);
    }, []);
    return(
        <View>
            <Header
                backgroundColor="rgb(253,201,0)"
                backgroundImageStyle={{}}
                barStyle="default"
                centerComponent={{
                    text: "Smart Home",
                    style: {color: "#fff" ,fontSize: 20, paddingBottom:5}
                }}
                centerContainerStyle={{}}
                // leftComponent={{ icon: "menu", color: "#fff" }}
                leftContainerStyle={{}}
                placement="center"
                // rightComponent={{ icon: "home", color: "#fff" }}
                rightContainerStyle={{}}
                statusBarProps={{}}
            />
            <View style = {styles.container}>
                <Text style = {styles.title}>Temperature</Text>
                <TouchableOpacity style={styles.buttonStyle}>
                    <Text style={styles.text_mid}>{temperature != null ? temperature : "-"}</Text>
                </TouchableOpacity>
                <Text style = {styles.title}>Brightness</Text>
                <TouchableOpacity style={styles.buttonStyle}>
                    <Text style = {styles.text_mid}>50</Text>
                </TouchableOpacity>
                <Text style = {styles.title}>Humidity</Text>
                <TouchableOpacity style={styles.buttonStyle}>
                    <Text style = {styles.text_mid}>80%</Text>
                </TouchableOpacity>
            </View>

            
            

        </View>
    )
}



export default HomePage
