import React, {useState} from 'react'
import { Text, View , Switch} from 'react-native'
import { Header } from "react-native-elements";
import styles from '../../elements/style';


const AIO_KEY = '<your Adafruit IO key>'; // Thay thế với Adafruit IO key của bạn
const FEED_ID = '<your feed ID>'; // Thay thế với feed ID của bạn
const AIO_USERNAME = '<your Adafruit IO username>'; // Thay thế với Adafruit IO username của bạn


const url = `https://io.adafruit.com/api/v2/${AIO_USERNAME}/feeds/${FEED_ID}/data`;
const data = { value };

fetch(url, {
    method: 'POST',
    headers: {
      'X-AIO-Key': AIO_KEY,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.error(error));


function set_value(value: boolean): void {
    fetch(url, {
        method: 'POST',
        headers: {
          'X-AIO-Key': AIO_KEY,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({value}),
      })
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(error => console.error(error));

}
// Fetch temperature data
fetch(`https://io.adafruit.com/api/v2/HarryHaha/feeds/temp-sensor/data/last`,
{
  headers: {
    'X-AIO-Key': 'aio_wHPt23RGoOQoK8MD32FJcfhbXWiS'
  }
})
.then(response => response.json())
.then(data => setTemperature(data.value))
.catch(error => console.error(error));

const SettingScreen: React.FC =() =>{
    const [isEnabled, setIsEnabled] = useState(false);
    const toggleSwitch = () => setIsEnabled(previousState => !previousState);

    const [isEnabled2, setIsEnabled2] = useState(false);
    const toggleSwitch2 = () => setIsEnabled2(previousState => !previousState);

    const [isEnabled3, setIsEnabled3] = useState(false);
    const toggleSwitch3 = () => setIsEnabled3(previousState => !previousState);
    return(
        <View>
            <Header
            backgroundColor="rgb(253,201,0)"
            backgroundImageStyle={{}}
            barStyle="default"
            centerComponent={{
                text: "Setting",
                style: {color: "#fff" ,fontSize: 20, paddingBottom:5}
            }}
            centerContainerStyle={{}}
            // leftComponent={{ icon: "menu", color: "#fff" }}
            leftContainerStyle={{}}
            placement="center"
            // rightComponent={{ icon: "home", color: "#fff" }}
            rightContainerStyle={{}}
            statusBarProps={{}}
            />

            <View style = {styles.container}>
                <Text style = {styles.title}>Light</Text>
                <Switch
                    trackColor={{false: '#767577', true: '#81b0ff'}}
                    thumbColor={isEnabled ? 'rgb(253,201,0)' : '#f4f3f4'}
                    ios_backgroundColor="#3e3e3e"
                    onValueChange={toggleSwitch}
                    value={isEnabled}
                    style = {styles.switch}
                />
                <Text style = {styles.title}>Fan</Text>
                <Switch
                    trackColor={{false: '#767577', true: '#81b0ff'}}
                    thumbColor={isEnabled2 ? 'rgb(253,201,0)' : '#f4f3f4'}
                    ios_backgroundColor="#3e3e3e"
                    onValueChange={toggleSwitch2}
                    value={isEnabled2}
                    style = {styles.switch}
                />

                <Text style = {styles.title}>Door</Text>
                <Switch
                    trackColor={{false: '#767577', true: '#81b0ff'}}
                    thumbColor={isEnabled3 ? 'rgb(253,201,0)' : '#f4f3f4'}
                    ios_backgroundColor="#3e3e3e"
                    onValueChange={toggleSwitch3}
                    value={isEnabled3}
                    style = {styles.switch}
                />
            </View>            
        </View>
    )
}

export default SettingScreen
