import React, {useState} from 'react'
import { Text, View, Alert, TouchableOpacity } from 'react-native'
import { Button, Header } from "react-native-elements";
import styles from '../../elements/style';
import { set_value } from './SettingScreen';


const AIO_KEY = 'aio_wHPt23RGoOQoK8MD32FJcfhbXWiS';
const AIO_USERNAME = 'HarryHaha';

const FEED_ID_1 = 'auto1';
const FEED_ID_2 = 'auto2';
const FEED_ID_3 = 'auto3';

const url_auto_1 = `https://io.adafruit.com/api/v2/${AIO_USERNAME}/feeds/${FEED_ID_1}/data`;
const url_auto_2 = `https://io.adafruit.com/api/v2/${AIO_USERNAME}/feeds/${FEED_ID_2}/data`;
const url_auto_3= `https://io.adafruit.com/api/v2/${AIO_USERNAME}/feeds/${FEED_ID_3}/data`;


const [buttonOpacity, setButtonOpacity] = useState(1.0);

  const handleOnPress = () => {
    setButtonOpacity(0.5); // thay đổi opacity của button
  }



let isEnabled1 = false;

const toggleSwitch1 = () => {   
    set_value(`${!isEnabled1 ? "1" : "0"}`,url_auto_1)
    isEnabled1 = !isEnabled1;
}
let isEnabled2 = false;

const toggleSwitch2 = () => {
    set_value(`${!isEnabled2 ? "1" : "0"}`,url_auto_2)
    isEnabled2 = !isEnabled2;
}
let isEnabled3 = false;

const toggleSwitch3 = () => {
    set_value(`${!isEnabled3 ? "1" : "0"}`,url_auto_3)
    isEnabled3 = !isEnabled3;
}

const AutoScreen: React.FC =() =>{
    return(
        <View>
            <Header
            backgroundColor="rgb(253,201,0)"
            backgroundImageStyle={{}}
            barStyle="default"
            centerComponent={{
                text: "Auto",
                style: {color: "#fff",fontSize: 20,paddingBottom:5 }
            }}
            centerContainerStyle={{}}
            // leftComponent={{ icon: "menu", color: "#fff" }}
            leftContainerStyle={{}}
            placement="center"
            // rightComponent={{ icon: "home", color: "#fff" }}
            rightContainerStyle={{}}
            statusBarProps={{}}
            />
            <View style = {styles.container}>
                <Text style={styles.title}>Recommends</Text>

                <TouchableOpacity 
                    onPress={handleOnPress}
                    style={[styles.buttonStyle, {opacity: buttonOpacity}]}
                    >
                    <Text style={styles.text}>Press Me</Text>
                    </TouchableOpacity>

                <TouchableOpacity style={[styles.buttonStyle, {backgroundColor: isEnabled1 ? "blue" : "red"}]} onPress={() => toggleSwitch1()}>
                  <Text style={styles.text}>Turn on the light when it's dark</Text>
                </TouchableOpacity>


                <TouchableOpacity style={styles.buttonStyle} onPress={() => toggleSwitch2()}>
                    <Text style={styles.text}>Turn on the fan when the temperature is over 20 degree</Text>
                </TouchableOpacity>

                <TouchableOpacity style={styles.buttonStyle} onPress={() => toggleSwitch3()}>
                    <Text style={styles.text}>Turn on the light when there are peole in the room</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}

export default AutoScreen
